mapper.select:
select 'false' as QUERYID,

from picture_map


    and id = #{id,jdbcType=INTEGER}


order by id desc
limit #{start}, #{size}

select count(0)
from picture_map


    and id = #{id,jdbcType=INTEGER}

select 'true' as QUERYID,

from picture_map
where id = #{id,jdbcType=INTEGER}

